    static void spamLines() throws InterruptedException {

        if (spam){
            Thread.sleep(1000);
            System.out.print(newLineClear);

        }

    }

    static void CalcLoop() throws InterruptedException {
        int inChar = 0;

        while (running) {
            if (!currentValue.equals(bigZero)) {
                System.out.println("The current value in the calc is " + currentValue+"\n");
            }
            Calculate();
            Thread.sleep(700);
            if(error){
                spam = true;
                return;
            }
            System.out.println("Another? Press X and Enter, in that order, to continue or any other key and enter to exit.");
            try {
                inChar = System.in.read();
                if (inChar != 120) {
                    return;
                } else if (inChar == 120) {
                    Scanner advance = new Scanner(System.in);
                    advance.nextLine();
                }
            } catch (IOException e) {
                System.out.println("Error reading from user");
            }
        }
    }

    static void Calculate() throws InterruptedException {
        String stringNum1 = "";
        String stringNum2 = "";
        String none = "0";
        char operator;
        char operator2;
        BigDecimal answerFloat = bigZero;
        Scanner stringScanner = new Scanner(System.in);
        BigDecimal float1 = new BigDecimal(none);
        BigDecimal float2 = new BigDecimal(none);
        BigDecimal previousAnswer = new BigDecimal(none);
        String newline = System.lineSeparator();


        //scanning input
        System.out.println("Put in first number");
        stringNum1 = stringScanner.nextLine();
        System.out.println("Put the second number");
        stringNum2 = stringScanner.nextLine();

        //error catcher and converting string to BigDecimal or BigInteger
        try {
            float2 = new BigDecimal(stringNum2);
            float1 = new BigDecimal(stringNum1);

        } catch (NumberFormatException e) {
            System.out.println("Literally told you numbers or periods for decimals only.");
            return;
        }
        System.out.println("What operator? + - * /");
        operator = stringScanner.next().charAt(0);


        //actually doing the operations
        try {
            switch (operator) {
                case '+' -> answerFloat = float1.add(float2);
                case '-' -> answerFloat = float1.subtract(float2);
                case '*' -> answerFloat = float1.multiply(float2);
                case '/' -> answerFloat = float1.divide(float2);

            }
        } catch (NullPointerException exception) {
            System.out.println("Wrong operator, which only happens if you put random characters in.");
        }

        //error checking and exit in event of error
        if (answerFloat.equals(bigZero) && !float1.equals(bigZero) && !float2.equals(bigZero)){
            System.out.println("Yes, "+float1+" "+operator+" "+float2+" equals "+answerFloat+". Love it. If you go any further, "+ newline +"the calc will break. I did my error testing >:)");
            Thread.sleep(500);
            error = true;
            return;
        }

        System.out.println("Here's your answer");
        System.out.println(stringNum1 + " " + operator + " " + stringNum2 + " = " + answerFloat);



        if (!currentValue.equals(bigZero)) {
            previousAnswer = answerFloat;
            System.out.println("What operator for the previous value? + - * /");
            operator2 = stringScanner.next().charAt(0);
            switch (operator2) {
                case '+' -> answerFloat = previousAnswer.add(currentValue);
                case '-' -> answerFloat = previousAnswer.subtract(currentValue);
                case '*' -> answerFloat = previousAnswer.multiply(currentValue);
                case '/' -> answerFloat = previousAnswer.divide(currentValue);
            }
            System.out.println("Here's your answer");
            System.out.println(stringNum1 + " " + operator + " " + stringNum2 + " " + previousAnswer + " " + operator2 + " " + currentValue + " = " + answerFloat);

        } else {
            currentValue = answerFloat;
        }

    }


